mapper.update:
UPDATE movies
SET
	movie_title = #{movie_title}
	, movie_nation = #{movie_nation}
	, movie_prdtYear = #{movie_prdtYear}
	, movie_director = #{movie_director}
	, movie_actor = #{movie_actor}
	, movie_genre = #{movie_genre}
	, movie_rating = #{movie_rating}
	, movie_plot = #{movie_plot}
	, movie_release_date = #{movie_release_date}
	, movie_close_date = #{movie_close_date}
	, movie_runtime = #{movie_runtime}
	, movie_poster = #{movie_poster}
	, movie_still1 = #{movie_still1}
	, movie_still2 = #{movie_still2}
	, movie_still3 = #{movie_still3}
	, movie_trailer = #{movie_trailer}
	, movie_audience = #{movie_audience}
	, movie_status = #{movie_status}
WHERE
	movie_id = #{movie_id}

mapper.select:
SELECT *
FROM movies
WHERE movie_id = #{movie_id}

SELECT * FROM cs JOIN theaters ON cs.theater_id = theaters.theater_id WHERE cs_type = '분실물문의'
AND (cs_subject LIKE '%${searchValue}%' OR theaters.theater_name LIKE '%${searchValue}%')
ORDER BY cs_id DESC LIMIT #{page.startRow}, #{page.listLimit}

mapper.update:
UPDATE cs
