WHERE cs_id = #{cs_id}

mapper.select:
SELECT payment_name, member_name, member_id, sum(quantity) AS quantity , GROUP_CONCAT(product_name) AS product_name, payment_datetime , payment_card_name, sum(payment_total_price) AS payment_total_price, payment_status FROM ordersView GROUP BY payment_name, member_name, member_id, payment_datetime, payment_status, payment_card_name
HAVING
member_id LIKE #{searchKeyword}
product_name LIKE '%${searchKeyword}%'
1=1
LIMIT #{page.startRow}, #{page.listLimit}

