mapper.select:
SELECT * FROM cs WHERE cs.cs_type = '자주묻는질문'
AND (cs.cs_subject LIKE '%${searchValue}%' OR cs.cs_type_detail LIKE '%${searchValue}%')
LIMIT #{startRow}, #{listLimit}

SELECT * FROM cs LEFT OUTER JOIN theaters ON cs.theater_id = theaters.theater_id WHERE cs.cs_type = '공지사항'
AND (cs.cs_subject LIKE '%${searchValue}%' OR theaters.theater_name LIKE '%${searchValue}%')
ORDER BY cs_date DESC LIMIT #{startRow}, #{listLimit}

SELECT *
FROM cs
WHERE cs_type = #{cs_type} 
	AND cs_type_list_num = #{cs_type_list_num}

mapper.insert:


	SELECT IFNULL(MAX(cs_type_list_num), 0)
	FROM cs
	WHERE cs_type = #{cs_type}

INSERT INTO cs
VALUES (
	null
	, #{cs_subject}
	, #{cs_content}
	, now()
	, #{cs_type}
	, #{cs_type_detail}
	, #{cs_file}
	, #{cs_type_list_num} + 1
	, null
	, #{member_id}
	
		
			, #{theater_id}
		
		
			, theater_id = null
		
	
)

