mapper.insert:
INSERT INTO members
VALUES (
	#{member_id} 
	, #{member_name}
	, #{member_email}
	, #{member_birth}
	, now()
	, #{member_phone}
	, 1
	
		
			, #{member_passwd}
		
		
			, ''
		
	
	, null
	, null
	, #{member_gender}
)

mapper.select:
SELECT * 
FROM members
WHERE member_id = #{member_id} OR member_email = #{member_email} OR member_phone = #{member_phone}

SELECT count(*)
FROM members
WHERE member_id = #{member_id}
