mapper.select:
SELECT seat_name
FROM resView 
WHERE play_date = #{play_date}
AND room_name = #{room_name}
AND theater_name = #{theater_name}
AND play_start_time = #{play_start_time}
AND seat_status = 1

SELECT room_name, play_start_time, play_end_time
FROM MPRT
WHERE play_date = #{play_date}
AND movie_title =#{movie_title}
AND theater_name = #{theater_name}

SELECT *
FROM MPRT 
WHERE


		movie_title = #{movie_title}
		AND theater_name = #{theater_name}


		movie_title = #{movie_title}
		AND play_date = #{play_date}


		theater_name = #{theater_name}
		AND play_date = #{play_date}


		movie_title = #{movie_title}


		theater_name = #{theater_name}


		play_date = #{play_date}


AND movie_status != 2
AND play_date >  Date(now())	
ORDER BY play_date ASC

SELECT play_end_time 
FROM MPRT  
WHERE movie_title = #{movie_title}
AND theater_name = #{theater_name}
AND play_date = #{play_date}
AND room_name = #{room_name}
AND play_start_time = #{play_start_time}

SELECT * FROM resView WHERE member_id = #{sId}
AND movie_title LIKE '%${searchKeyword}%'
ORDER BY payment_id DESC LIMIT #{page.startRow}, #{page.listLimit}

mapper.insert:
INSERT INTO seats
VALUES(
	null
	,#{seat_name}
	,1
	,(SELECT play_id 
	  FROM PRT   
	  WHERE play_date = #{play_date}
	  AND play_start_time = #{play_start_time}
	  AND room_name = #{room_name}
	  AND theater_name = #{theater_name})
)

INSERT INTO payments
VALUES(
	null
	,(SELECT CONCAT('P',prt.play_id ,'S',s.seat_id)
	  FROM PRT prt JOIN seats s
	  ON prt.play_id = s.play_id
	  WHERE seat_name = #{seat_name}
	  AND play_date = #{play_date}
	  AND play_start_time = #{play_start_time}
	)
	,now()
	,'카드이름'
	,'카드번호'
	,#{payment_total_price}
	,1			
)

INSERT INTO reserve
VALUES(
	(SELECT payment_id
	FROM payments
	WHERE payment_name = (SELECT CONCAT('P',prt.play_id ,'S',s.seat_id)
						  FROM PRT prt JOIN seats s
						  ON prt.play_id = s.play_id
						  WHERE s.seat_name = #{seat_name}
						  AND prt.play_date = #{play_date}
						  AND prt.play_start_time = #{play_start_time}
						)
	)
	,#{sId}
	,(SELECT seat_id
	 FROM seats
	 WHERE seat_name = #{seat_name}
	 AND play_id = (SELECT play_id 
				    FROM PRT   
				    WHERE play_date = #{play_date}
				    AND play_start_time = #{play_start_time}
				    AND room_name = #{room_name}
				    AND theater_name = #{theater_name}
				    )
	)
)

mapper.select:
SELECT *
FROM resView
WHERE
	movie_title = #{movie_title}
	AND theater_name = #{theater_name}
	AND room_name = #{room_name}
	AND play_date = #{play_date}
	AND play_start_time = #{play_start_time}
	AND seat_name = #{seat_name}
	AND payment_total_price = #{payment_total_price}
	AND member_id = #{sId}

SELECT * FROM resView WHERE payment_name LIKE 'P%'
AND member_id = #{sId}
ORDER BY payment_id DESC LIMIT 2
